{
  "openapi": "3.0.0",
  "info": {
    "title": "Movie Database API",
    "description": "An intentionally vulnerable Movie Database API for DAST education.",
    "version": "1.0.0"
  },
  "servers": [
    {
      "url": "http://localhost:5000",
      "description": "Local development server"
    }
  ],
  "paths": {
    "/movies": {
      "get": {
        "summary": "Retrieve all movies",
        "description": "Fetches a list of all movies from the database.",
        "responses": {
          "200": {
            "description": "A list of movies",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/Movie"
                  }
                }
              }
            }
          }
        }
      }
    },
    "/add_movie": {
      "post": {
        "summary": "Add a new movie",
        "description": "Adds a new movie to the database.",
        "requestBody": {
          "required": true,
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "$ref": "#/components/schemas/MovieInput"
              }
            }
          }
        },
        "responses": {
          "302": {
            "description": "Redirect to movie list after successful addition"
          }
        }
      }
    },
    "/search_movie": {
      "get": {
        "summary": "Search for a movie",
        "description": "Searches for a movie by name. **Vulnerable to SQL Injection**.",
        "parameters": [
          {
            "name": "search",
            "in": "query",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Search results",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/Movie"
                  }
                }
              }
            }
          }
        }
      }
    },
    "/reviews/{movie_id}": {
      "get": {
        "summary": "Retrieve reviews for a movie",
        "parameters": [
          {
            "name": "movie_id",
            "in": "path",
            "required": true,
            "schema": {
              "type": "integer"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "A list of reviews",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/Review"
                  }
                }
              }
            }
          }
        }
      },
      "post": {
        "summary": "Add a review to a movie",
        "description": "Adds a review to a movie. **Vulnerable to XSS attacks.**",
        "parameters": [
          {
            "name": "movie_id",
            "in": "path",
            "required": true,
            "schema": {
              "type": "integer"
            }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "$ref": "#/components/schemas/ReviewInput"
              }
            }
          }
        },
        "responses": {
          "302": {
            "description": "Redirect to movie reviews after submission"
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Movie": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer"
          },
          "name": {
            "type": "string"
          },
          "director": {
            "type": "string"
          },
          "cast": {
            "type": "string"
          },
          "rating": {
            "type": "string"
          },
          "description": {
            "type": "string"
          }
        }
      },
      "MovieInput": {
        "type": "object",
        "properties": {
          "movie_name": {
            "type": "string"
          },
          "director": {
            "type": "string"
          },
          "cast": {
            "type": "string"
          },
          "rating": {
            "type": "string"
          },
          "description": {
            "type": "string"
          }
        }
      },
      "Review": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer"
          },
          "movie_id": {
            "type": "integer"
          },
          "review": {
            "type": "string"
          }
        }
      },
      "ReviewInput": {
        "type": "object",
        "properties": {
          "review": {
            "type": "string"
          }
        }
      }
    }
  }
}

